
def whichFloor(input):
	floor = 0
	for p in input:
		if p == '(':
			floor += 1
		if p == ')':
			floor -= 1
	return floor

def firstBasement(input):
	floor = 0
	count = 0
	for p in input:
		count += 1
		if p == '(':
			floor += 1
		if p == ')':
			floor -= 1
			if floor < 0:
				return count


